mapper.select:
SELECT pa.pno AS pno, pa.pname AS pname, s.sno AS sno, s.sname AS sname, thesis.file_path AS
    file_path, oes.score AS score
FROM project_select_result psr LEFT JOIN student s ON psr.sno = s.sno LEFT JOIN project_apply pa
    ON psr.pno = pa.pno LEFT JOIN thesis ON psr.pno = thesis.pno LEFT JOIN oral_exam_score oes ON s.sno = oes.sno
WHERE s.group_num = #{groupNum} AND s.school_year = #{schoolYear} AND s.college = #{college}

  AND pa.pname LIKE #{oralExamSearchDTO.pname,jdbcType=VARCHAR}


  AND s.sname LIKE #{oralExamSearchDTO.sname,jdbcType=VARCHAR}
