mapper.select:
SELECT pa.pno AS pno,pa.pname AS pname, t.tno AS creator_no ,t.tname AS creator_name,pa.type AS type
FROM project_apply pa LEFT JOIN teacher t ON pa.creator_no = t.tno
WHERE pa.is_select != 1 AND pa.is_pass = 1
AND pa.creator_no regexp #{reg} AND pa.school_year=#{schoolYear} AND pa.college=#{college}

  AND pa.pname LIKE #{projectSearchDTO.pname,jdbcType=VARCHAR}


  AND t.tname LIKE #{projectSearchDTO.creatorName,jdbcType=VARCHAR}


  AND type LIKE #{projectSearchDTO.type,jdbcType=VARCHAR}


  AND tags LIKE #{projectSearchDTO.type,jdbcType=VARCHAR}

SELECT pa.pno AS pno,pa.pname AS pname, s.sno AS creator_no ,s.sname AS creator_name,pa.type AS type
FROM project_apply pa LEFT JOIN student s ON pa.creator_no = s.sno
WHERE pa.is_select != 1 AND pa.is_pass = 1
AND pa.creator_no regexp #{reg} AND pa.school_year=#{schoolYear} AND pa.college=#{college}

  AND pa.pname LIKE #{projectSearchDTO.pname,jdbcType=VARCHAR}


  AND s.sname LIKE #{projectSearchDTO.creatorName,jdbcType=VARCHAR}


  AND type LIKE #{projectSearchDTO.type,jdbcType=VARCHAR}


  AND tags LIKE #{projectSearchDTO.type,jdbcType=VARCHAR}
